import React, { Component } from 'react';
import { Card, CardTitle, CardImg, CardBody, CardText, CardImgOverlay } from 'reactstrap';

class DishDetail extends Component {

    constructor(props) {
        super(props);
    }

    renderDish(dish) {
        if (dish != null) {
            return (
                <Card>
                    <CardImg top src={dish.image} alt={dish.name} />
                    <CardBody>
                        <CardTitle>{dish.name}</CardTitle>
                        <CardText>{dish.description}</CardText>
                    </CardBody>
                </Card>
            );

        }
        else
            return (
                <div></div >
            );


    }

    renderComments(dish_comments) {
        if (this.props.s_dish != null) {
            dish_comments.map((comment_obj) => {
                return (
                    <div key={dish_comments._id}>
                        <li>{comment_obj.comment}</li>
                        <li>--{comment_obj.author} , {comment_obj.date}</li>
                    </div>

                );
            });
        } else {
            return (
                <div></div>
            );
        }
    }

    render() {
        return (
            <>
                <div className="col-12 col-md-5 m-1">
                    {this.renderDish(this.props.s_dish)}
                </div>
                <div className="col-12 col-md-5 m-1">
                    {
                    this.renderComments(this.props.s_dish.comments)
                    }
                </div>
            </>


        );
    }


}

export default DishDetail;
