import React, { Component } from 'react';

class Comments extends Component {
    constructor(props) {
        super(props);
    }

    RenderComments(comments_array) {
        const arr_comments = this.props.dish_comment.comments.every(function (comment) {
            if (this.props.dish_comment != null) {
                console.log(comment);
                return (
                    <p>{comment}</p>
                );
            }
            else {
                return (
                    <div></div>
                );
            }
        });
    }


    render() {
        return (
            this.RenderComments(this.props.s_dish.comments)
        );
    }
}

export default Comments;
