import React, { Component } from 'react';

class Comments extends Component {
    constructor(props) {
        super(props);
    }

    RenderComments(comments_array) {
        const arr = comments_array.map(comment => {
            return (
                <p>{comment}</p>
            );
        });
    }


    render() {
        if (this.props.dish_comment != null) {
            const arr = this.props.dish_comment.comments.map(comment => {
                return (
                    <p>{comment}</p>
                );
            });
        }

        return (
            { arr }
        );
    }
}

export default Comments;
