import React, { Component } from 'react';

class Comments extends Component {
    constructor(props) {
        super(props);
    }

    RenderDishComments(dish) {
        if (dish != null) {
            const arr = dish.comments.map(comment => {
                return (
                    <p>{comment}</p>
                );
            });
        } else {
            return (
                <div></div>
            );
        }
    }

    render() {
        return (
            this.RenderDishComments(this.props.dish_comment)
        );
    }
}

export default Comments;
